import request from '@/utils/request';
export default {
  getList: function getList(params) {
    return request({
      url: '/shop/goods/list',
      method: 'get',
      params: params
    });
  },
  saveBaseInfo: function saveBaseInfo(params) {
    return request({
      url: '/shop/goods/saveBaseInfo',
      method: 'post',
      data: params
    });
  },
  save: function save(params) {
    return request({
      url: '/shop/goods',
      method: 'post',
      data: params
    });
  },
  remove: function remove(id) {
    return request({
      url: '/shop/goods',
      method: 'delete',
      params: {
        id: id
      }
    });
  },
  get: function get(id) {
    return request({
      url: '/shop/goods',
      method: 'get',
      params: {
        id: id
      }
    });
  },
  changeIsOnSale: function changeIsOnSale(id, isOnSale) {
    return request({
      url: '/shop/goods/changeIsOnSale',
      method: 'post',
      params: {
        id: id,
        isOnSale: isOnSale
      }
    });
  }
};
