import request from '@/utils/request';
var apiUrl = process.env.BASE_API;
export function getApiUrl() {
  return apiUrl;
}
export function getList(params) {
  return request({
    url: '/banner/list',
    method: 'get',
    params: params
  });
}
export function save(params) {
  return request({
    url: '/banner',
    method: 'post',
    params: params
  });
}
export function remove(id) {
  return request({
    url: '/banner',
    method: 'delete',
    params: {
      id: id
    }
  });
}
