import request from '@/utils/request';
export default {
  getList: function getList(params) {
    return request({
      url: '/promotion/topic/list',
      method: 'get',
      params: params
    });
  },
  save: function save(params) {
    return request({
      url: '/promotion/topic',
      method: 'post',
      params: params
    });
  },
  remove: function remove(id) {
    return request({
      url: '/promotion/topic',
      method: 'delete',
      params: {
        id: id
      }
    });
  },
  changeDisabled: function changeDisabled(id, disabled) {
    return request({
      url: '/promotion/topic/changeDisabled',
      method: 'post',
      params: {
        id: id,
        disabled: disabled
      }
    });
  }
};
