import treeTable from '@/components/TreeTable';
import { list, save as _save, del } from '@/api/system/dept';
import permission from '@/directive/permission/index.js';
export default {
  directives: {
    permission: permission
  },
  name: 'dept',
  components: {
    treeTable: treeTable
  },
  data: function data() {
    return {
      expandAll: true,
      data: [],
      formVisible: false,
      formTitle: '',
      isAdd: false,
      deptTree: {
        data: []
      },
      form: {
        id: '',
        simplename: '',
        fullname: '',
        pid: undefined,
        num: ''
      },
      rules: {
        simplename: [{
          required: true,
          message: '请输入菜单名称',
          trigger: 'blur'
        }, {
          min: 3,
          max: 20,
          message: '长度在 3 到 20 个字符',
          trigger: 'blur'
        }],
        fullname: [{
          required: true,
          message: '请输入编码',
          trigger: 'blur'
        }, {
          min: 2,
          max: 20,
          message: '长度在 2 到 20 个字符',
          trigger: 'blur'
        }],
        num: [{
          required: true,
          message: '请输入排序',
          trigger: 'blur'
        }]
      }
    };
  },
  created: function created() {
    this.fetchData();
  },
  methods: {
    fetchData: function fetchData() {
      var _this = this;
      this.listLoading = true;
      list().then(function (response) {
        _this.data = response.data;
        _this.deptTree.data = response.data;
        _this.listLoading = false;
      });
    },
    checkSel: function checkSel() {
      if (this.selRow && this.selRow.id) {
        return true;
      }
      this.$message({
        message: '请选中操作项',
        type: 'warning'
      });
      return false;
    },
    add: function add() {
      this.form = {};
      this.formTitle = '添加菜单';
      this.formVisible = true;
      this.isAdd = true;
    },
    save: function save() {
      var _this2 = this;
      var self = this;
      this.$refs['form'].validate(function (valid) {
        if (valid) {
          var menuData = {
            id: self.form.id,
            simplename: self.form.simplename,
            fullname: self.form.fullname,
            num: self.form.num,
            pid: self.form.pid
          }; //self.form
          menuData.parent = null;
          _save(menuData).then(function (response) {
            _this2.$message({
              message: '提交成功',
              type: 'success'
            });
            self.fetchData();
            self.formVisible = false;
          });
        } else {
          return false;
        }
      });
    },
    editItem: function editItem(record) {
      this.selRow = Object.assign({}, record);
      if (this.selRow.pid == '0') {
        this.selRow.pid = undefined;
      }
      this.edit();
    },
    edit: function edit() {
      if (this.checkSel()) {
        this.isAdd = false;
        this.form = Object.assign({}, this.selRow);
        this.form.statusBool = this.form.statusName === '启用';
        this.form.password = '';
        this.formTitle = '修改用户';
        this.formVisible = true;
      }
    },
    removeItem: function removeItem(record) {
      this.selRow = record;
      this.remove();
    },
    remove: function remove() {
      var _this3 = this;
      if (this.checkSel()) {
        this.$confirm('确定删除该记录?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(function () {
          var row = _this3.selRow;
          del(row.id).then(function (response) {
            _this3.$message({
              message: '删除成功',
              type: 'success'
            });
            _this3.fetchData();
          });
        });
      }
    }
  }
};
