import { getUserInfo, save } from '@/api/shop/shopUser';
import { getList } from '@/api/shop/address';
import { getApiUrl } from '@/utils/utils';
export default {
  name: 'shopUserDetail',
  data: function data() {
    return {
      userInfo: {
        info: {}
      },
      idUser: '',
      apiUrl: getApiUrl(),
      addressList: [],
      avatarUrl: ''
    };
  },
