import request from '@/utils/request';
export default {
  getAttrVals: function getAttrVals(idAttrKey) {
    return request({
      url: '/shop/attr/val/getAttrVals',
      method: 'get',
      params: {
        idAttrKey: idAttrKey
      }
    });
  },
  save: function save(params) {
    return request({
      url: '/shop/attr/val',
      method: 'post',
      params: params
    });
  },
  remove: function remove(id) {
    return request({
      url: '/shop/attr/val',
      method: 'delete',
      params: {
        id: id
      }
    });
  },
  updateAttrVal: function updateAttrVal(id, attrVal) {
    return request({
      url: '/shop/attr/val/updateAttrVal',
      method: 'POST',
      params: {
        id: id,
        attrVal: attrVal
      }
    });
  },
  getAttrBy: function getAttrBy(idCategory) {
    return request({
      url: '/shop/attr/val/getAttrByCategoryAndGoods/' + idCategory,
      method: 'get'
    });
  }
};
