import treeTable from '@/components/TreeTable';
import { getList, save as _save, delMenu, getMenuTree } from '@/api/system/menu';
import permission from '@/directive/permission/index.js';
import IconSelect from '@/components/IconSelect';
export default {
  directives: {
    permission: permission
  },
  name: 'menu',
  components: {
    treeTable: treeTable,
    IconSelect: IconSelect
  },
  data: function data() {
    return {
      showTree: false,
      defaultProps: {
        id: 'code',
        label: 'name',
        children: 'children'
      },
      listLoading: true,
      expandAll: false,
      formTitle: '',
      formVisible: false,
      isAdd: false,
      form: {
        id: '',
        name: '',
        code: '',
        url: '',
        pcode: '',
        ismenu: 1,
        hidden: false,
        num: 1
      },
      rules: {
        name: [{
          required: true,
          message: '请输入菜单名称',
          trigger: 'blur'
        }, {
          min: 2,
          max: 20,
          message: '长度在 2 到 20 个字符',
          trigger: 'blur'
        }],
        code: [{
          required: true,
          message: '请输入编码',
          trigger: 'blur'
        }, {
          min: 2,
          max: 20,
          message: '长度在 2 到 20 个字符',
          trigger: 'blur'
        }],
        url: [{
          required: true,
          message: '请输入资源地址',
          trigger: 'blur'
        }],
        num: [{
          required: true,
          message: '请输入排序',
          trigger: 'blur'
        }]
      },
      data: [],
      treeData: [],
      selRow: {}
    };
  },
  created: function created() {
    this.init();
  },
  methods: {
    // 选择图标
    selected: function selected(name) {
      this.form.icon = name;
      this.$forceUpdate();
    },
    init: function init() {
      var _this = this;
      this.fetchData();
      getMenuTree().then(function (response) {
        _this.treeData = response.data;
      });
    },
    fetchData: function fetchData() {
      var _this2 = this;
      this.listLoading = true;
      getList().then(function (response) {
        _this2.data = response.data;
        _this2.listLoading = false;
      });
    },
    checkSel: function checkSel() {
      if (this.selRow && this.selRow.id) {
        return true;
      }
      this.$message({
        message: '请选中操作项',
        type: 'warning'
      });
      return false;
    },
    add: function add() {
      this.form = {
        ismenu: 1,
        hidden: false
      };
      this.formTitle = '添加菜单';
      this.formVisible = true;
      this.isAdd = true;
      if (this.$refs['form'] !== undefined) {
        this.$refs['form'].resetFields();
      }
    },
    save: function save() {
      var _this3 = this;
      var self = this;
      this.$refs['form'].validate(function (valid) {
        if (valid) {
          var menuData = self.form;
          delete menuData.parent;
          delete menuData.children;
          _save(menuData).then(function (response) {
            _this3.$message({
              message: '提交成功',
              type: 'success'
            });
            self.fetchData();
            self.formVisible = false;
          });
        } else {
          return false;
        }
      });
    },
    edit: function edit(row) {
      this.form = Object.assign({}, row);
      if (row.isMenuName === '是') {
        this.form.ismenu = 1;
      } else {
        this.form.ismenu = 0;
      }
      if (this.form.pcode == '0') {
        this.form.pcode = undefined;
      }
      this.formTitle = '编辑菜单';
      this.formVisible = true;
      this.isAdd = false;
    },
    remove: function remove(row) {
      var _this4 = this;
      this.$confirm('确定删除该记录?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        delMenu(row.id).then(function (response) {
          _this4.$message({
            message: '删除成功',
            type: 'success'
          });
          _this4.fetchData();
        }).catch(function (err) {
          _this4.$notify.error({
            title: '错误',
            message: err
          });
        });
      });
    },
    componentTips: function componentTips() {
      this.$notify({
        title: '提示',
        dangerouslyUseHTMLString: true,
        message: '顶级目录请输入layout,<br/>左侧菜单请根据实际组件路径输入:views/...<br/>功能按钮无需输入该值'
      });
    }
  }
};
