import { getList, remove, save } from '@/api/shop/shopUser';
import { getApiUrl } from '@/utils/utils';
export default {
  name: 'shopUser',
  data: function data() {
    return {
      formVisible: false,
      formTitle: '添加用户',
      isAdd: true,
      form: {
        mobile: '',
        salt: '',
        password: '',
        nickName: '',
        avatar: '',
        id: ''
      },
      regDate: undefined,
      lastLoginTime: undefined,
      listQuery: {
        page: 1,
        limit: 20,
        mobile: undefined,
        nickName: undefined,
        startRegDate: undefined,
        endRegDate: undefined,
        startLastLoginTime: undefined,
        endLastLoginTime: undefined
      },
      total: 0,
      list: null,
      listLoading: true,
      selRow: {}
    };
  },
  filters: {
    statusFilter: function statusFilter(status) {
      var statusMap = {
        published: 'success',
        draft: 'gray',
        deleted: 'danger'
      };
      return statusMap[status];
    }
  },
  created: function created() {
    this.init();
  },
  methods: {
    init: function init() {
      this.fetchData();
    },
    fetchData: function fetchData() {
      var _this = this;
      this.listLoading = true;
      if (this.regDate) {
        this.listQuery['startRegDate'] = this.regDate[0];
        this.listQuery['endRegDate'] = this.regDate[1];
      }
      if (this.lastLoginTime) {
        this.listQuery['startLastLoginTime'] = this.lastLoginTime[0];
        this.listQuery['endLastLoginTime'] = this.lastLoginTime[1];
      }
      getList(this.listQuery).then(function (response) {
        _this.list = response.data.records;
        for (var index in _this.list) {
          var item = _this.list[index];
          if (item.wechatHeadImgUrl) {
            item.img = item.wechatHeadImgUrl;
          } else {
            item.img = getApiUrl() + '/file/getImgStream?idFile=' + item.avatar;
          }
        }
        _this.listLoading = false;
        _this.total = response.data.total;
      });
    },
    search: function search() {
      this.listQuery.page = 1;
      this.fetchData();
    },
    reset: function reset() {
      this.listQuery.mobile = '';
      this.listQuery.nickName = '';
      this.listQuery.startRegDate = '';
      this.listQuery.endRegDate = '';
      this.listQuery.startLastLoginTime = '';
      this.listQuery.endLastLoginTime = '';
      this.lastLoginTime = '';
      this.regDate = '';
      this.listQuery.page = 1;
      this.fetchData();
    },
    handleFilter: function handleFilter() {
      this.listQuery.page = 1;
      this.getList();
    },
    handleClose: function handleClose() {},
    fetchNext: function fetchNext() {
      this.listQuery.page = this.listQuery.page + 1;
      this.fetchData();
    },
    fetchPrev: function fetchPrev() {
      this.listQuery.page = this.listQuery.page - 1;
      this.fetchData();
    },
    fetchPage: function fetchPage(page) {
      this.listQuery.page = page;
      this.fetchData();
    },
    changeSize: function changeSize(limit) {
      this.listQuery.limit = limit;
      this.fetchData();
    },
    handleCurrentChange: function handleCurrentChange(currentRow, oldCurrentRow) {
      this.selRow = currentRow;
    },
    checkSel: function checkSel() {
      if (this.selRow && this.selRow.id) {
        return true;
      }
      this.$message({
        message: this.$t('common.mustSelectOne'),
        type: 'warning'
      });
      return false;
    }
  }
};
