import "core-js/modules/web.dom.iterable";
import topicApi from '@/api/promotion/topic';
import goodsApi from '@/api/shop/goods';
import articleApi from '@/api/cms/article';
import { getApiUrl } from '@/utils/utils';
import permission from '@/directive/permission/index.js';
export default {
  name: 'topic',
  directives: {
    permission: permission
  },
  data: function data() {
    return {
      formVisible: false,
      formTitle: '添加专题',
      isAdd: true,
      form: {
        title: '',
        idArticle: '',
        idGoodsList: '',
        pv: '',
        id: ''
      },
      listQuery: {
        page: 1,
        limit: 20,
        id: undefined,
        disabled: ''
      },
      options: [{
        label: '是',
        value: 1
      }, {
        label: '否',
        value: 0
      }],
      rangeDate: '',
      total: 0,
      list: null,
      listLoading: true,
      selRow: {},
      apiUrl: getApiUrl(),
      showArticle: false,
      article: {
        title: '',
        content: ''
      },
      goodsList: [],
      selectedGoods: [],
      articleList: [],
      searchLoading: false,
      showTopic: false,
      topicUrl: '',
      topicDetail: ''
    };
  },
  filters: {
    statusFilter: function statusFilter(status) {
      var statusMap = {
        published: 'success',
        draft: 'gray',
        deleted: 'danger'
      };
      return statusMap[status];
    }
  },
  computed: {
    //表单验证
    rules: function rules() {
      return {
        // cfgName: [
        //   { required: true, message: this.$t('config.name') + this.$t('common.isRequired'), trigger: 'blur' },
        //   { min: 3, max: 2000, message: this.$t('config.name') + this.$t('config.lengthValidation'), trigger: 'blur' }
        // ]
      };
    }
  },
  created: function created() {
    this.init();
  },
  methods: {
    init: function init() {
      this.fetchData();
    },
    fetchData: function fetchData() {
      var _this = this;
      this.listLoading = true;
      var queryData = this.listQuery;
      if (this.rangeDate) {
        queryData['startDate'] = this.rangeDate[0];
        queryData['endDate'] = this.rangeDate[1];
      }
      topicApi.getList(this.listQuery).then(function (response) {
        _this.list = response.data.records;
        _this.listLoading = false;
        _this.total = response.data.total;
      });
    },
    search: function search() {
      this.listQuery.page = 1;
      this.fetchData();
    },
    reset: function reset() {
      this.listQuery.id = '';
      this.listQuery.disabled = '';
      this.rangeDate = '';
      this.listQuery.page = 1;
      this.fetchData();
    },
    handleFilter: function handleFilter() {
      this.listQuery.page = 1;
      this.getList();
    },
    handleClose: function handleClose() {},
    fetchNext: function fetchNext() {
      this.listQuery.page = this.listQuery.page + 1;
      this.fetchData();
    },
    fetchPrev: function fetchPrev() {
      this.listQuery.page = this.listQuery.page - 1;
      this.fetchData();
    },
    fetchPage: function fetchPage(page) {
      this.listQuery.page = page;
      this.fetchData();
    },
    changeSize: function changeSize(limit) {
      this.listQuery.limit = limit;
      this.fetchData();
    },
    handleCurrentChange: function handleCurrentChange(currentRow, oldCurrentRow) {
      this.selRow = currentRow;
    },
    resetForm: function resetForm() {
      this.form = {
        title: '',
        idArticle: '',
        idGoodsList: '',
        pv: '',
        id: ''
      };
    },
    add: function add() {
      this.resetForm();
      this.formTitle = '添加专题', this.formVisible = true;
      this.isAdd = true;
    },
    save: function save() {
      var _this2 = this;
      this.$refs['form'].validate(function (valid) {
        if (valid) {
          topicApi.save({
            title: _this2.form.title,
            idArticle: _this2.form.idArticle,
            idGoodsList: _this2.form.idGoodsList.join(','),
            pv: _this2.form.pv,
            id: _this2.form.id
          }).then(function (response) {
            _this2.$message({
              message: _this2.$t('common.optionSuccess'),
              type: 'success'
            });
            _this2.fetchData();
            _this2.formVisible = false;
          });
        } else {
          return false;
        }
      });
    },
    checkSel: function checkSel() {
      if (this.selRow && this.selRow.id) {
        return true;
      }
      this.$message({
        message: this.$t('common.mustSelectOne'),
        type: 'warning'
      });
      return false;
    },
    edit: function edit() {
      if (this.checkSel()) {
        this.isAdd = false;
        this.form = this.selRow;
        this.formTitle = '编辑专题';
        this.formVisible = true;
      }
    },
    remove: function remove() {
      var _this3 = this;
      if (this.checkSel()) {
        var id = this.selRow.id;
        this.$confirm(this.$t('common.deleteConfirm'), this.$t('common.tooltip'), {
          confirmButtonText: this.$t('button.submit'),
          cancelButtonText: this.$t('button.cancel'),
          type: 'warning'
        }).then(function () {
          topicApi.remove(id).then(function (response) {
            _this3.$message({
              message: _this3.$t('common.optionSuccess'),
              type: 'success'
            });
            _this3.fetchData();
          }).catch(function (err) {
            _this3.$notify.error({
              title: '错误',
              message: err
            });
          });
        }).catch(function () {});
      }
    },
    viewArticle: function viewArticle(article) {
      this.article = article;
      this.showArticle = true;
    },
    changeDisabled: function changeDisabled(data) {
      var _this4 = this;
      topicApi.changeDisabled(data.id, data.disabled).then(function (response) {
        _this4.$message({
          message: data.disabled ? '禁用成功' : '启用成功',
          type: 'success'
        });
      });
    },
    searchGoods: function searchGoods(query) {
      var _this5 = this;
      if (query !== '') {
        this.searchLoading = true;
        goodsApi.getList({
          page: 1,
          limit: 20,
          name: query
        }).then(function (response) {
          var goodsList = response.data.records;
          if (_this5.selectedGoods.length > 0) {
            //将已选择的商品列表和新的搜索结果列表合并为新的下拉列表【和changeGoods中的处理方式呼应】
            _this5.selectedGoods.forEach(function (item, index, arr) {
              var hasInArr = false;
              goodsList.forEach(function (item2, index2, arr2) {
                if (item2.id === item.id) {
                  hasInArr = true;
                }
              });
              if (!hasInArr) {
                goodsList.push(item);
              }
            });
          }
          _this5.searchLoading = false;
          _this5.goodsList = goodsList;
        });
      } else {
        this.goodsList = [];
      }
    },
    changeGoods: function changeGoods(val) {
      //选中一个值的时候，将该值存放在以选择列表中，以便下一次模糊搜索的时候将该列表和搜索结果列表合并为新的下拉列表【临时处理多次搜索的时候导致前面已选中结果label丢失的情况，暂时没有更好的解决方法，先这么处理了】
      var goodsList = this.goodsList;
      var selectedGoods = new Array();
      val.forEach(function (item, index, arr) {
        goodsList.forEach(function (item2, index2, arr2) {
          if (item2.id === item) {
            selectedGoods.push(item2);
          }
        });
      });
      this.selectedGoods = selectedGoods;
    },
    searchArticle: function searchArticle(query) {
      var _this6 = this;
      if (query !== '') {
        this.searchLoading = true;
        articleApi.getList({
          page: 1,
          limit: 20,
          title: query
        }).then(function (response) {
          _this6.articleList = response.data.records;
          _this6.searchLoading = false;
        });
      } else {
        this.goodsList = [];
      }
    },
    view: function view(id) {
      //todo 预览使用了手机h5站点的地址,后期可以将h5 站点地址配置在系统参数中
      this.topicDetail = ' <iframe src="https://linjiashop.microapp.store/#/topic/' + id + '" width="100%" height="667px" frameborder="0" scrolling="auto"></iframe>';
      this.showTopic = true;
    }
  }
};
