import request from '@/utils/request';
export default {
  getList: function getList(params) {
    return request({
      url: '/shop/category/list',
      method: 'get',
      params: params
    });
  },
  getCategories: function getCategories() {
    return request({
      url: '/shop/category/getAll',
      method: 'get'
    });
  },
  getBanners: function getBanners(id) {
    return request({
      url: '/shop/category/getBanners/' + id,
      method: 'get'
    });
  },
  getAttrKeys: function getAttrKeys(id) {
    return request({
      url: '/shop/category/getAttrKeys/' + id,
      method: 'get'
    });
  },
  save: function save(params) {
    return request({
      url: '/shop/category',
      method: 'post',
      params: params
    });
  },
  remove: function remove(id) {
    return request({
      url: '/shop/category',
      method: 'delete',
      params: {
        id: id
      }
    });
  },
  removeBanner: function removeBanner(id, idBanner) {
    return request({
      url: '/shop/category/removeBanner/' + id + '/' + idBanner,
      method: 'delete'
    });
  },
  setCategoryBanner: function setCategoryBanner(id, idBanner) {
    return request({
      url: '/shop/category/setBanner/' + id + '/' + idBanner,
      method: 'post'
    });
  },
  changeShowIndex: function changeShowIndex(id, showIndex) {
    return request({
      url: '/shop/category/changeShowIndex/' + id + '/' + showIndex,
      method: 'post'
    });
  }
};
