import request from '@/utils/request';
export default {
  save: function save(params) {
    return request({
      url: '/user/order/save',
      method: 'post',
      params: params
    });
  },
  get: function get(orderSn) {
    return request({
      url: '/user/order/' + orderSn,
      method: 'get'
    });
  },
  /**
   * 获取物流信息
   * @param orderSn
   */
  getExpressInfo: function getExpressInfo(orderSn) {
    return request({
      url: '/user/order/getExpressInfo/' + orderSn,
      method: 'get'
    });
  },
  /**
   * 取消订单
   * @param orderSn
   */
  remove: function remove(orderSn) {
    return request({
      url: '/user/order/cancel/' + orderSn,
      method: 'post'
    });
  },
  /**
   * 获取订单列表
   * @param params
   */
  getOrders: function getOrders(params) {
    return request({
      url: '/user/order/getOrders',
      method: 'get',
      params: params
    });
  },
  /**
   * 准备付款
   * @param params
   */
  prepareCheckout: function prepareCheckout(params) {
    return request({
      url: '/user/order/prepareCheckout',
      params: params,
      method: 'get'
    });
  },
  /**
   * 确认收货
   * @param params
   */
  confirm: function confirm(orderSn) {
    return request({
      url: '/user/order/confirm/' + orderSn,
      method: 'post'
    });
  }
};
