import "core-js/modules/es6.regexp.to-string";
import CryptoJS from 'crypto-js/crypto-js';
var KEY = CryptoJS.enc.Utf8.parse("v80v8g2ta05rrg2k"); //  密钥        长度必须为16位
var IV = CryptoJS.enc.Utf8.parse("6gycoeef15nql8ab"); //  初始向量    长度随意
export default {
  //加密
  encrypt: function encrypt(word) {
    var key = KEY;
    var iv = IV;
    var srcs = CryptoJS.enc.Utf8.parse(word);
    var encrypted = CryptoJS.AES.encrypt(srcs, key, {
      iv: iv,
      mode: CryptoJS.mode.CBC,
      padding: CryptoJS.pad.ZeroPadding
    });
    // console.log("-=-=-=-", encrypted.ciphertext)
    return CryptoJS.enc.Base64.stringify(encrypted.ciphertext);
  },
  //解密
  decrypt: function decrypt(word) {
    var key = KEY;
    var iv = IV;
    var base64 = CryptoJS.enc.Base64.parse(word);
    var src = CryptoJS.enc.Base64.stringify(base64);
    var decrypt = CryptoJS.AES.decrypt(src, key, {
      iv: iv,
      mode: CryptoJS.mode.CBC,
      padding: CryptoJS.pad.ZeroPadding
    });
    var decryptedStr = decrypt.toString(CryptoJS.enc.Utf8);
    return decryptedStr.toString();
  }
};
