import request from '@/utils/request';
export default {
  getUserInfo: function getUserInfo() {
    return request({
      url: '/user/getInfo',
      method: 'get'
    });
  },
  updateUserName: function updateUserName(userName) {
    var params = {
      userName: userName
    };
    return request({
      url: '/user/updateUserName/',
      method: 'post',
      params: params
    });
  },
  updatePwd: function updatePwd(params) {
    return request({
      url: '/account/updatePwd',
      method: 'post',
      params: params
    });
  },
  updateGender: function updateGender(gender) {
    return request({
      url: '/user/updateGender/' + gender,
      method: 'post'
    });
  },
  updatePassword: function updatePassword(oldPwd, password, rePassword) {
    return request({
      url: '/user/updatePassword/' + oldPwd + '/' + password + '/' + rePassword,
      method: 'post'
    });
  },
  upload: function upload(file) {
    return request({
      url: '/file/upload/base64',
      method: 'post',
      data: file
    });
  },
  sendSmsCode: function sendSmsCode(mobile) {
    return request({
      url: '/user/sendSmsCode',
      method: 'post',
      params: {
        "mobile": mobile
      }
    });
  },
  getWxSign: function getWxSign(params) {
    return request({
      url: '/user/getWxSign',
      method: 'post',
      params: params
    });
  },
  getWxOpenId: function getWxOpenId(params) {
    return request({
      url: '/user/getWxOpenId',
      method: 'post',
      params: params
    });
  }
};
