// import Cookies from 'js-cookie'

var COOKIE_KEY_PRE = "lite-shop-mobile-";
var tokenKey = COOKIE_KEY_PRE + "token";
export default {
  getToken: function getToken() {
    return localStorage.getItem(tokenKey);
  },
  setToken: function setToken(token) {
    localStorage.setItem(tokenKey, token);
  },
  setUser: function setUser(user) {
    this.set('user', JSON.stringify(user));
  },
  getUser: function getUser() {
    var user = JSON.parse(this.get('user'));
    return user;
  },
  removeToken: function removeToken() {
    localStorage.removeItem(tokenKey);
  },
  set: function set(key, value) {
    localStorage.setItem(COOKIE_KEY_PRE + key, value);
  },
  get: function get(key) {
    return localStorage.getItem(COOKIE_KEY_PRE + key);
  }
};
